    const handleCityMove = (e) => {
        console.log(e.target.x)
    }


    const cityArray = cities.map((item) => <City key={item.key} data={item} handleCityMove={handleCityMove} />)
    const lineArray = lines.map((item) => <Road key={item.key} data={item} />)

    // console.log(cityArray.length > 0 ? 'true' : 'false')

    return (
        <Stage width={window.innerWidth} height={(window.innerHeight) - ((window.innerHeight) * .05)} onClick={handleStageClick}>
            <Layer>
                {lineArray}
                {/* {lastLine ? <Road key={lastLine.key} data={lastLine } /> : null} */}
            </Layer>
            <Layer>
                {cityArray}
            </Layer>
        </Stage>
    );
}

export default MainStage
