import { useState, useEffect } from 'react'
import MainStage from './components/MainStage'
import Header from './components/Header'
import InfoWindow from './components/InfoWindow'
import { v4 as uuidv4 } from 'uuid';


function rFact(num)
{
    if (num === 0)
      { return 1; }
    else
      { return num * rFact( num - 1 ); }
}

function getCount(num){
  if (num === 0){
    return 0
  } else if (num === 1) {
    return 1
  }else{
    return (rFact(num-1))/2
  }
}

function App() {


  const [cities, setCities] = useState([])
  // const [solCount, setSolCount] = useState(0)


  const handleClearClick = () => {

    setCities([])

  }


  const handleStageClick = (e) => {

      let r = Math.floor(Math.random() * 255);
      let g = Math.floor(Math.random() * 255);
      let b = Math.floor(Math.random() * 255);

      console.log(e)

      const newCity = {
          key: uuidv4(),
          name: uuidv4(),
          x: e.evt.offsetX,
          y: e.evt.offsetY,
          fill: `rgb(${r},${g},${b})`,
      }
      setCities([...cities, newCity])
      // setSolCount(solutionCount(cities.length))
  }


 let solCount = getCount(cities.length)




  return (
    <div className="App">
      <Header handleClearClick={handleClearClick}/>
      <InfoWindow solCount={solCount} />
      <MainStage handleStageClick={handleStageClick} cities={cities}/>
    </div>
  );
}

export default App;
