import React from 'react'

const InfoWindow = ({cities}) => {

    function calcSolutions(x) {
        let n = x - 1
        let fac = (n != 1) ? n * calcSolutions(n - 1) : 1;

        return fac/2
      }


    console.log(calcSolutions(10))


    return (
        <div className="info-window">

            <h2>
                Stats
            </h2>
            <ul>
                <li>Number of solutions:</li>
                <li>{((cities.length) - 1)}</li>
            </ul>

        </div>
    )
}

export default InfoWindow
