import React, {useState} from 'react';
import { render } from 'react-dom';
import { Stage, Layer, Rect, Text, Circle, Line } from 'react-konva';
import City from './City'
import { v4 as uuidv4 } from 'uuid';


const MainStage = () => {

    const [cities, setCities] = useState([])

    const handleStageClick = (e) => {



        const newCity = {
            id: uuidv4(),
            name: uuidv4(),
            x: e.evt.x,
            y: e.evt.y,
            fill: '#ffffff',
        }

        setCities([...cities, newCity])

    }

    const cityArray = cities.map((item) => <City key={item.key} data={item}/>)

    return (
        <Stage width={window.innerWidth} height={window.innerHeight} onClick={handleStageClick}>
            <Layer>

            {/* Render a component on click, maybe keep track of components to render in a state array? */}
            {/* render object array here */}
            {cityArray}

            </Layer>
        </Stage>
    );
}

export default MainStage
