import React, { useState } from 'react'

const InfoWindow = ({ cities }) => {

    // const [solCount, setSolCount] = useState(0)

    // function fac(n) {
    //     return (n != 1) ? n * fac(n - 1) : 1;
    //   }
    // const solutionCount = (n) => {

    //     return (fac(n-1))/2

    // }
    function rFact(num) {
        if (num === 0) { return 1; }
        else { return num * rFact(num - 1); }
    }

    function getCount(num) {
        console.log(num)
        if (num === 0) {
            return 0
        } else if (num === 1) {
            return 1
        } else if (num === 2) {
            return 1
        } else {
            return (rFact(num - 1)) / 2
        }
    }



    return (
        <div className="info-window">

            <h2>
                Stats
            </h2>
            <ul>
                <li>Number of cities:</li>
                <li>{cities.length}</li>
                <li>Number of unique solutions:</li>
                <li>{getCount(cities.length)}</li>
            </ul>

        </div>
    )
}

export default InfoWindow
