import React, {useState, useEffect} from 'react';
import { render } from 'react-dom';
import { Stage, Layer, Rect, Text, Circle, Line } from 'react-konva';
import City from './City'
import { v4 as uuidv4 } from 'uuid';
import Road from './Road';


const MainStage = ({handleStageClick, cities}) => {

    const [lines, setLines] = useState([])

    const handleClick = (e) => {
        //call handle stage click and add a line
        handleStageClick(e)
        let starting = cities[cities.length-1]

        if (cities.length !== 0){
            console.log(starting)
            let newLine = {
                key: uuidv4(),
                points: [starting.x, starting.y, e.evt.offsetX, e.evt.offsetY],
                stroke: 'red'
            }

            setLines([...lines, newLine])
        }
    }




    const cityArray = cities.map((item) => <City key={item.key} data={item}/>)
    const lineArray = lines.map((item) => <Road key={item.key} data={item} />)

    return (
        <Stage width={window.innerWidth} height={(window.innerHeight) - ((window.innerHeight)*.05)} onClick={handleClick}>
            <Layer>
                {cityArray}
            </Layer>
            <Layer>
                {lineArray}
            </Layer>
        </Stage>
    );
}

export default MainStage
