import React, {useState} from 'react';
import { render } from 'react-dom';
import { Stage, Layer, Rect, Text, Circle, Line } from 'react-konva';
import City from './City'
import { v4 as uuidv4 } from 'uuid';


const MainStage = ({handleStageClick, cities}) => {



    const cityArray = cities.map((item) => <City key={item.key} data={item}/>)

    return (
        <Stage width={window.innerWidth} height={(window.innerHeight) - ((window.innerHeight)*.1)} onClick={handleStageClick}>
            <Layer>
                {cityArray}
            </Layer>
        </Stage>
    );
}

export default MainStage
