import { useState, useEffect } from 'react'
import MainStage from './components/MainStage'
import Header from './components/Header'
import InfoWindow from './components/InfoWindow'
import { v4 as uuidv4 } from 'uuid';


function rFact(num) {
  if (num === 0) { return 1; }
  else { return num * rFact(num - 1); }
}

function getCount(num) {
  if (num === 0) {
    return 0
  } else if (num === 1) {
    return 1
  } else if (num === 2) {
    return 1
  } else {
    return (rFact(num - 1)) / 2
  }
}

function App() {

  const [lines, setLines] = useState([])
  const [cities, setCities] = useState([])

  const [lastLine, setLastLine] = useState(null)

  const handleClearClick = () => {

    setCities([])

  }


  const handleGenClick = () => {

    console.log(cities)

  }


  const handleStageClick = (e) => {

    let r = Math.floor(Math.random() * 255);
    let g = Math.floor(Math.random() * 255);
    let b = Math.floor(Math.random() * 255);


    const newCity = {
      key: uuidv4(),
      name: uuidv4(),
      x: e.evt.offsetX,
      y: e.evt.offsetY,
      fill: `rgb(${r},${g},${b})`,
    }
    setCities(() => [...cities, newCity])


    let starting = cities[cities.length-1]

    console.log(cities.length)

    if (cities.length !== 0) {

      let newLine = {
        key: uuidv4(),
        startName: starting.name,
        points: [starting.x, starting.y, e.evt.offsetX, e.evt.offsetY],
        stroke: 'red'
      } 

      if (cities.length >= 2) {

        let first = cities[0]

        let newLastLine = {
          key: uuidv4(),
          points: [first.x, first.y, e.evt.offsetX, e.evt.offsetY],
          stroke: 'red'
        }

        setLastLine(newLastLine)

      }

      setLines([...lines, newLine])
    } 


  }







  return (
    <div className="App">
      <Header handleClearClick={handleClearClick} />
      <InfoWindow handleGenClick={handleGenClick} cities={cities} />
      <MainStage handleStageClick={handleStageClick} cities={cities} lines={lines} lastLine={lastLine}/>
    </div>
  );
}

export default App;
